import React, {useState} from 'react';
import { TextField, Button, Switch, FormControlLabel } from "@material-ui/core";

function FormularioCadastro(){
    const [nome, setNome ] = useState("Adriano");
    const [sobreNome, setSobrenome ] = useState("Carvalho");

return(
   <form onSubmit={(event) => {
       event.preventDefault();
       console.log(nome);
   }}>

       <TextField 
        value={nome} 
        onChange={(event) => {
            setNome(event.target.value);

            if(nome.length <= 3){
                setNome(nome.substr(0,3));
            }
        }}

       id="nome" label="Nome" variant="outlined" color="secondary" margin="normal" fullWidth/>

       <TextField
            value={nome} 
        onChange={(event) => {
            setSobrenome(event.target.value);

            if(nome.length <= 3){
                setSobrenome(nome.substr(0,3));
            }
        }}
       id="Sobrenome" label="Sobrenome" variant="outlined" color="secondary" margin="normal" fullWidth/>
       <TextField id="CPF" label="CPF" variant="outlined" color="secondary" margin="normal" fullWidth/>

       <FormControlLabel label="Promoções" control={ 
            <Switch defaultChecked name="promocoes" color="primary" />
        } />

      <FormControlLabel label="Novidades" control={ 
            <Switch defaultChecked name="promocnovidadesoes" color="primary" />
        } />

       <Button variant="contained" color="primary">Cadastrar</Button>

   </form>  

);
}

export default FormularioCadastro;
